import React , { useState } from "react";
import Profil from "./Profil";
import Right from "./Right";
import Default from "./Defult";
import userContext from "./userContext";


export default function Body(){

    let [chang ,setchange]=useState(false)
    let [order,setorder]=useState([])
    let [newOrder,set]=useState('')
    let addOrder=(element)=>{
        setorder(t=>[...t,element])

    }

    let dele=(e)=>{
        setnewOrder(e)
    }


    return (
        <div className="body">
           
           {chang ? <Right column='4/6' rows='1/6'  getorder={order} mnue={dele} /> : <Default  column='4/6' rows='1/6'/> }
            
            
            <Profil changdiv={setchange} order={addOrder} info={{
                column:'1' ,
                rows:'1' ,
                image:'/image/image-waffle-desktop.jpg' , 
                thename:"Waffle" ,
                discrption:'Waffle with Berries' ,
                slarey:'$6.50' ,
                class:'order' ,
            }}
                
                
            />
            
            <Profil changdiv={setchange} order={addOrder} info={{
                column:'2'  ,
                rows:'1' ,
                image:'/image/image-creme-brulee-desktop.jpg'  ,
                thename:"Crème Brûlée" ,
                discrption:'Vanilla Bean Crème Brûlée', 
                slarey:'$7.00' ,
                class:'order',
            }}
                
            />
             
             <Profil changdiv={setchange} order={addOrder} info={{
                column:'3',  
                rows:'1',
                image:'/image/image-macaron-desktop.jpg' , 
                thename:"Macaron" ,
                discrption:'Macaron Mix of Five' ,
                slarey:'$8.00' ,
                class:'order'
             }}
             
                /> 

             <Profil changdiv={setchange} order={addOrder} info={{
                column:'1'  ,
                rows:'2' ,
                image:'/image/image-tiramisu-desktop.jpg'  ,
                thename:"Tiramisu" ,
                discrption:'Classic Tiramisu' ,
                slarey:'$5.50', 
                class:'order' 
             }} 
               /> 

             <Profil changdiv={setchange} order={addOrder} info={{
                 column:'2'  ,
                 rows:'2' ,
                 image:'/image/image-baklava-desktop.jpg'  ,
                 thename:"Baklava" ,
                 discrption:'Pistachio Baklava' ,
                 slarey:'$4.00' ,
                 class:'order'
             }}
            /> 

            <Profil changdiv={setchange} order={addOrder} info={{
                column:'3'  ,
                rows:'2' ,
                image:'/image/image-meringue-desktop.jpg'  ,
                thename:"Pie" ,
                discrption:'Lemon Meringue Pie' ,
                slarey:'$5.00' ,
                class:'order' 

            }}
                 /> 

             <Profil changdiv={setchange} order={addOrder} info={{
                column:'1'  ,
                rows:'3' ,
                image:'/image/image-cake-desktop.jpg'  ,
                thename:"Cake" ,
                discrption:'Red Velvet Cake', 
                slarey:'$4.50' ,
                class:'order'
             }}
                  /> 

             <Profil changdiv={setchange} order={addOrder}  info={{
                column:'2'  ,
                rows:'3' ,
                image:'/image/image-brownie-desktop.jpg'  ,
                thename:"Brownie" ,
                discrption:'Salted Caramel Brownie' ,
                slarey:'$5.50' ,
                class:'order' 
             }}
             /> 

             <Profil changdiv={setchange} order={addOrder}  info={{
                column:'3' , 
                rows:'3' ,
                image:'/image/image-panna-cotta-desktop.jpg'  ,
                thename:"Panna Cotta" ,
                discrption:'Vanilla Panna Cotta' ,
                slarey:'$6.50' ,
                class:'order' 
             }}
                 

             /> 
            
            

        </div>
    );
}
