        return cartitem.find((item)=>item.id ===id)?.quantity || 0
    }
    const increase=(id)=>{
        setcartitem()
    }
    return (
    <userContext.Provider value={{cartitem}}>
        {Children}
    </userContext.Provider>
    )
}
export default Shopping ;
