import React, { useCallback, useContext, useRef, useState } from "react";
import userContext from "./userContext";
import { useCart } from "react-use-cart";
import { flushSync } from "react-dom";
import prodect from "./data";
// import Navbar from "./Navbar";
function Profil(prop){
    let [mins,setmins]=useState(1)
    let [button,setbutton]=useState(false)
    let getdiv=useRef()
    let {item}=prodect
    let but_mins=()=>{
        setmins(mins-1)
        prop.redu(-prop.info.slarey--)
        
        }

    let but_plas=()=>{  
        setmins(mins+1)
        prop.redu(+prop.info.slarey++)
        }
    
        let fun=(e)=>{
                prop.changdiv(true)
                setbutton(true)
                prop.order(item)
                prop.redu(item)
                prop.data(item)
                
            }

                // e.target.style.display='none'
            // console.log(e.target.className)
            // e.target.classList.add('hidde')
            // prop.swich(true)
            // prop.handle()
        return (
            <>
            
            {item.map((element)=>{
            return (
            <div className="card" style={{gridColumn:element.column,gridRow:element.rows}}>
            
            <div className="card-item" style={{backgroundImage:`url(${element.image})`}}>
                
                {/* <button id="basic" onClick={fun} className={prop.info.class} >
                <svg xmlns="http://www.w3.org/2000/svg" width="28" height="20" fill="none" viewBox="0 0 21 20"><g fill="#C73B0F" clip-path="url(#a)"><path d="M6.583 18.75a1.25 1.25 0 1 0 0-2.5 1.25 1.25 0 0 0 0 2.5ZM15.334 18.75a1.25 1.25 0 1 0 0-2.5 1.25 1.25 0 0 0 0 2.5ZM3.446 1.752a.625.625 0 0 0-.613-.502h-2.5V2.5h1.988l2.4 11.998a.625.625 0 0 0 .612.502h11.25v-1.25H5.847l-.5-2.5h11.238a.625.625 0 0 0 .61-.49l1.417-6.385h-1.28L16.083 10H5.096l-1.65-8.248Z"/><path d="M11.584 3.75v-2.5h-1.25v2.5h-2.5V5h2.5v2.5h1.25V5h2.5V3.75h-2.5Z"/></g><defs><clipPath id="a"><path fill="#fff" d="M.333 0h20v20h-20z"/></clipPath></defs></svg>
                Add to Cart
                    </button> */}
    
                    {button ? <div className="other" ref={getdiv}><button className="cyrcle" onClick={but_mins}>-</button>{mins}<button className="cyrcle" onClick={but_plas} >+</button></div> :
    
                    <button id="basic" onClick={fun} key={element.id} className={element.class} >
                    <svg xmlns="http://www.w3.org/2000/svg" width="28" height="20" fill="none" viewBox="0 0 21 20"><g fill="#C73B0F" clip-path="url(#a)"><path d="M6.583 18.75a1.25 1.25 0 1 0 0-2.5 1.25 1.25 0 0 0 0 2.5ZM15.334 18.75a1.25 1.25 0 1 0 0-2.5 1.25 1.25 0 0 0 0 2.5ZM3.446 1.752a.625.625 0 0 0-.613-.502h-2.5V2.5h1.988l2.4 11.998a.625.625 0 0 0 .612.502h11.25v-1.25H5.847l-.5-2.5h11.238a.625.625 0 0 0 .61-.49l1.417-6.385h-1.28L16.083 10H5.096l-1.65-8.248Z"/><path d="M11.584 3.75v-2.5h-1.25v2.5h-2.5V5h2.5v2.5h1.25V5h2.5V3.75h-2.5Z"/></g><defs><clipPath id="a"><path fill="#fff" d="M.333 0h20v20h-20z"/></clipPath></defs></svg>
                    Add to Cart
                    </button>
                    }
                    
            </div>
            <div className="string">
                <span>{element.thename}</span>
                <span>{element.discrption}</span>
                <span className="slarey">{element.slarey}</span>
            </div>
            </div>
            

                )
        })
        
        }
        </>
    )
    }
            

        

export default Profil
