import React from "react";
import { flushSync } from "react-dom";
function Right(prop){
    // [].reduce()
    let dele=(index)=>{
        let filt=prop.getorder.filter((_,i)=> i !== index)
        prop.dele(filt)   
        // prop.swich(false)
    }
    // console.log(prop.sendReduce)
    
    let marg = prop.sendReduce.reduce(function(a,b){
        if(prop.pdata.id===prop.id){
            return +a+b
        }
           
        })
    console.log(prop.pdata)
    
    return (
        <div className="div-right">

            
            <div className="cart" style={{gridColumn:prop.column}}>
            <h1>hello</h1>
            <div className="mnue">
                <ul>
                    {prop.getorder.map((item,index)=>{
                        
                        return <li key={index} >
                           <span >{item}</span> 
                            <span>
                                ${marg}
                            </span>
                        <button onClick={()=>dele(index)}>delete</button>
                        
                        </li>
                    })}

                </ul>
                
            </div>
            <button className="confirm" >confirm</button>
            </div>
        </div>
    )
}
export default Right;
