            Add to Cart
                </button>

                {but && <div className="other ">
               
               <button className="cyrcle" onClick={but_mins}>-</button>
                   {mins}
               <button className="cyrcle" onClick={but_plas} >+</button>
                </div>
           }
                
        </div>
        <div className="string">
            <span>{prop.info.thename}</span>
            <span>{prop.info.discrption}</span>
            <span>{prop.info.slarey}</span>
        </div>
        </div>
        </>
    );

